TOR_HEC_spLP_contract = w3.eth.contract(address=TOR_HEC_spLP_ADDRESS, abi=TOR_HEC_spLP_ABI)
USDC_HEC_spLP_contract = w3.eth.contract(address=USDC_HEC_spLP_ADDRESS, abi=USDC_HEC_spLP_ABI)
HEC_contract = w3.eth.contract(address=HEC_ADDRESS, abi=HEC_ABI)
FNFT_contract = w3.eth.contract(address=FNFT_ADDRESS, abi=FNFT_ABI)

event_signature_hash = w3.keccak(text='FNFTMinted(address,address,uint256,uint256,uint256)').hex()

topic_HEC = '0x0000000000000000000000005c4fdfc5233f935f20d2adba572f770c2e377ab0'
topic_spLP_TOR = '0x0000000000000000000000004339b475399ad7226be3ad2826e1d78bbfb9a0d9'
topic_spLP_USDC = '0x0000000000000000000000000b9589a2c1379138d4cc5043ce551f466193c8de'

def get_voting_power_HEC(event:dict) -> Decimal:
    return w3.from_wei(int(event['data'].hex()[2:66], 16), 'gwei')

def get_voting_power_spLP_TOR(event:dict) -> Decimal:
    block = event['blockNumber']
    spLP_total = w3.from_wei(TOR_HEC_spLP_contract.caller.totalSupply(block_identifier=block), 'ether')
    spLP_reserves = TOR_HEC_spLP_contract.caller.getReserves(block_identifier=block)
    spLP_HEC_reserve = w3.from_wei(spLP_reserves[0], 'gwei')
    spLP_used = w3.from_wei(int(event['data'].hex()[2:66], 16), 'ether')
    ownership_pct = spLP_used/spLP_total
    voting_power = ownership_pct * spLP_HEC_reserve
    return voting_power

def get_voting_power_spLP_USDC(event:dict) -> Decimal:
    block = event['blockNumber']
    spLP_total = w3.from_wei(USDC_HEC_spLP_contract.caller.totalSupply(block_identifier=block), 'ether')
    spLP_reserves = USDC_HEC_spLP_contract.caller.getReserves(block_identifier=block)
    spLP_HEC_reserve = w3.from_wei(spLP_reserves[1], 'gwei')
    spLP_used = w3.from_wei(int(event['data'].hex()[2:66], 16), 'ether')
    ownership_pct = spLP_used/spLP_total
    voting_power = ownership_pct * spLP_HEC_reserve
    return voting_power

def parse_event(event:dict):
    nft_id = int(event['topics'][3].hex(), 16)
    if event['topics'][1].hex() == topic_HEC:
        return nft_id, get_voting_power_HEC(event), 'HEC'
    elif event['topics'][1].hex() == topic_spLP_TOR:
        return nft_id, get_voting_power_spLP_TOR(event), 'spLP_TOR'
    elif event['topics'][1].hex() == topic_spLP_USDC:
        return nft_id, get_voting_power_spLP_USDC(event), 'spLP_USDC'

@scheduler.task('interval', id='manage_new_events', seconds=CHECK_INTERVAL, misfire_grace_time=900)
def manage_new_events():
    logging.info('Checking for new events...')
    global fNFTs_full
    global fNFTs_short
    global last_checked_block
    newest_block = w3.eth.block_number
    filter = {
        'fromBlock': last_checked_block,
        'toBlock': last_checked_block+3000,
        'address': '0x1fA6693d9933CC7f578CDd35071FC6d6cc8451E0',
        'topics': [event_signature_hash],
        }
    while newest_block > last_checked_block:
        new_events = w3.eth.get_logs(filter)
        
        for event in new_events:
            fnft_id , voting_power, mint_type = parse_event(event)
            try:
                owner = FNFT_contract.caller.ownerOf(fnft_id)
            except ContractLogicError:
                owner = None
            # add to full list
            fNFTs_full[fnft_id] = {
                'voting_power': float(round(voting_power, 2)),
                'owner': owner,
                'mint_type': mint_type
            }
            # add to short list, as float rounded to 2 decimal places
            if owner:
                fNFTs_short.append({fnft_id:float(round(voting_power, 2))})
                fNFTs_short_with_boosts.append({fnft_id:float(round(voting_power, 2))})

            else:
                fNFTs_short.append({fnft_id:0.0})
                fNFTs_short_with_boosts.append({fnft_id:0.0})
            print(f'{fnft_id=}  {fNFTs_full[fnft_id]}')

        # increase block range
        last_checked_block = min(last_checked_block+3000, newest_block)
        filter['fromBlock'] = last_checked_block
        filter['toBlock'] = last_checked_block + 3000
    
    if FIRST_RUN: # add boosts from og api, only on first run
        for i in range(10067):
            fNFTs_short_with_boosts[i] = {i:float(hec_api[i][str(i)])}

    logging.info(f'Finished checking for new events. Last checked block {last_checked_block}')


def at_exit():
    scheduler.shutdown()
    with open('last_checked_block.json', 'w') as f:
        json.dump(last_checked_block, f)
    with open('fNFTs_full.json', 'w') as f:
        json.dump(fNFTs_full, f, indent=4)
    with open('fNFTs_short.json', 'w') as f:
        json.dump(fNFTs_short, f, indent=4)
    with open('fNFTs_short_with_boosts.json', 'w') as f:
        json.dump(fNFTs_short_with_boosts, f, indent=4)

atexit.register(at_exit)
app = Flask(__name__)

@app.route('/voting-power')
def get_voting_power():
    return fNFTs_short

@app.route('/voting-power-with-boosts')
def get_voting_power_with_boosts():
    return fNFTs_short_with_boosts

@app.route('/last-checked-block')
def get_last_checked_block():
    return jsonify({'block': last_checked_block})
